sourcesContent:
import fetch from 'node-fetch';
export const dolar=async()=>{

    try {
        const indicadores= await fetch('https://mindicador.cl/api')
        const res= await indicadores.json()
        return res
    } catch (error) {
        console.log("Error en la API de los indicadores ecnomicos ", error)
        return 
    }
};
